CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "role" varchar not null default 'user', "avatar" varchar, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "keyword" varchar, "slug" varchar not null, "order_num" integer not null default '0', "is_dropdown" tinyint(1) not null default '0', "is_public" tinyint(1) not null default '0', "is_visible" tinyint(1) not null default '0', "is_editable" tinyint(1) not null default '1', "image" varchar, "deleted_at" datetime, "page_id" integer, "created_by" integer, foreign key("page_id") references "pages"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "pages_uniqid_unique" on "pages" ("uniqid");
CREATE TABLE IF NOT EXISTS "sections" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "component" varchar not null default '0', "title" varchar, "content" text, "order_num" integer not null default '0', "is_visible" tinyint(1) not null default '1', "image" varchar, "deleted_at" datetime, "page_id" integer, "created_by" integer, foreign key("page_id") references "pages"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "sections_uniqid_unique" on "sections" ("uniqid");
CREATE TABLE IF NOT EXISTS "file_section" ("id" integer primary key autoincrement not null, "section_id" integer not null, "file" varchar not null, "deleted_at" datetime, foreign key("section_id") references "sections"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "bookmarks" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "uniqid_user" varchar not null, "uniqid_obj" varchar not null, "classe" varchar not null);
CREATE TABLE IF NOT EXISTS "capabilities" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "effect" varchar, "level" integer not null default '1', "pa" varchar, "po" varchar not null default '1', "po_editable" tinyint(1) not null default '1', "time_before_use_again" varchar, "casting_time" varchar, "duration" varchar, "element" integer not null default '0', "is_magic" tinyint(1) not null default '1', "ritual_available" tinyint(1) not null default '1', "powerful" integer not null default '2', "usable" tinyint(1) not null default '0', "is_visible" tinyint(1) not null default '0', "image" varchar, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "capabilities_uniqid_unique" on "capabilities" ("uniqid");
CREATE TABLE IF NOT EXISTS "classes" ("id" integer primary key autoincrement not null, "official_id" varchar, "dofusdb_id" varchar, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description_fast" varchar, "description" varchar, "life" varchar, "life_dice" integer not null default '10', "specificity" varchar, "usable" tinyint(1) not null default '0', "dofus_version" varchar not null default '3', "is_visible" tinyint(1) not null default '0', "image" varchar, "icon" varchar, "auto_update" tinyint(1) not null default '1', "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "classes_uniqid_unique" on "classes" ("uniqid");
CREATE TABLE IF NOT EXISTS "classe_spell" ("classe_id" integer not null, "spell_id" integer not null, "deleted_at" datetime, foreign key("classe_id") references "classes"("id") on delete cascade, foreign key("spell_id") references "spells"("id") on delete cascade, primary key ("classe_id", "spell_id"));
CREATE TABLE IF NOT EXISTS "capability_classe" ("capability_id" integer not null, "classe_id" integer not null, "deleted_at" datetime, foreign key("capability_id") references "capabilities"("id") on delete cascade, foreign key("classe_id") references "classes"("id") on delete cascade, primary key ("capability_id", "classe_id"));
CREATE TABLE IF NOT EXISTS "attribute_classe" ("classe_id" integer not null, "attribute_id" integer not null, "deleted_at" datetime, foreign key("classe_id") references "classes"("id") on delete cascade, foreign key("attribute_id") references "attributes"("id") on delete cascade, primary key ("classe_id", "attribute_id"));
CREATE TABLE IF NOT EXISTS "conditions" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "is_unbewitchable" tinyint(1) not null default '0', "is_malus" tinyint(1) not null default '1', "usable" tinyint(1) not null default '0', "is_visible" tinyint(1) not null default '0', "image" varchar, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "conditions_uniqid_unique" on "conditions" ("uniqid");
CREATE TABLE IF NOT EXISTS "consumables" ("id" integer primary key autoincrement not null, "official_id" varchar, "dofusdb_id" varchar, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "effect" varchar, "level" integer, "recepe" varchar, "price" varchar, "rarity" integer not null default '5', "usable" tinyint(1) not null default '0', "dofus_version" varchar not null default '3', "is_visible" tinyint(1) not null default '0', "image" varchar, "auto_update" tinyint(1) not null default '1', "deleted_at" datetime, "consumabletype_id" integer, "created_by" integer, foreign key("consumabletype_id") references "consumabletypes"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "consumables_uniqid_unique" on "consumables" ("uniqid");
CREATE TABLE IF NOT EXISTS "consumable_resource" ("consumable_id" integer not null, "resource_id" integer not null, "quantity" varchar not null default '1', "deleted_at" datetime, foreign key("consumable_id") references "consumables"("id") on delete cascade, foreign key("resource_id") references "resources"("id") on delete cascade, primary key ("consumable_id", "resource_id"));
CREATE TABLE IF NOT EXISTS "items" ("id" integer primary key autoincrement not null, "official_id" varchar, "dofusdb_id" varchar, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "level" integer not null default '1', "description" varchar, "effect" varchar not null, "bonus" varchar, "recepe" varchar, "actif" varchar not null default '0', "twohands" varchar not null default '0', "pa" varchar not null default '1', "po" varchar not null default '1', "price" varchar, "rarity" integer not null default '5', "usable" tinyint(1) not null default '0', "dofus_version" varchar not null default '3', "is_visible" tinyint(1) not null default '0', "image" varchar, "auto_update" tinyint(1) not null default '1', "deleted_at" datetime, "itemtype_id" integer, "created_by" integer, foreign key("itemtype_id") references "itemtypes"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "items_uniqid_unique" on "items" ("uniqid");
CREATE TABLE IF NOT EXISTS "item_resource" ("item_id" integer not null, "resource_id" integer not null, "quantity" varchar not null default '1', "deleted_at" datetime, foreign key("item_id") references "items"("id") on delete cascade, foreign key("resource_id") references "resources"("id") on delete cascade, primary key ("item_id", "resource_id"));
CREATE TABLE IF NOT EXISTS "item_panoply" ("item_id" integer not null, "panoply_id" integer not null, "deleted_at" datetime, foreign key("item_id") references "items"("id") on delete cascade, foreign key("panoply_id") references "panoplies"("id") on delete cascade, primary key ("item_id", "panoply_id"));
CREATE TABLE IF NOT EXISTS "mobs" ("id" integer primary key autoincrement not null, "creature_id" integer, "official_id" varchar, "dofusdb_id" varchar, "dofus_version" varchar not null default '3', "auto_update" tinyint(1) not null default '1', "size" integer not null default '2', "mobrace_id" integer, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("mobrace_id") references "mobraces"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "npcs" ("id" integer primary key autoincrement not null, "creature_id" integer, "story" varchar, "historical" varchar, "age" varchar, "size" varchar, "classe_id" integer, "specialization_id" integer, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("classe_id") references "classes"("id") on delete cascade, foreign key("specialization_id") references "specializations"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "resources" ("id" integer primary key autoincrement not null, "dofusdb_id" varchar, "official_id" integer, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "level" integer not null default '1', "price" varchar, "weight" varchar, "rarity" integer not null default '5', "usable" tinyint(1) not null default '1', "dofus_version" varchar not null default '3', "is_visible" tinyint(1) not null default '0', "image" varchar, "auto_update" tinyint(1) not null default '1', "deleted_at" datetime, "resourcetype_id" integer, "created_by" integer, foreign key("resourcetype_id") references "resourcetypes"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "resources_uniqid_unique" on "resources" ("uniqid");
CREATE TABLE IF NOT EXISTS "shops" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "location" varchar, "price" integer not null default '0', "usable" tinyint(1) not null default '1', "is_visible" tinyint(1) not null default '0', "image" varchar, "deleted_at" datetime, "created_by" integer, "npc_id" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "shops_uniqid_unique" on "shops" ("uniqid");
CREATE TABLE IF NOT EXISTS "consumable_shop" ("consumable_id" integer not null, "shop_id" integer not null, "quantity" varchar, "price" varchar, "comment" varchar, "deleted_at" datetime, foreign key("consumable_id") references "consumables"("id") on delete cascade, foreign key("shop_id") references "shops"("id") on delete cascade, primary key ("consumable_id", "shop_id"));
CREATE TABLE IF NOT EXISTS "item_shop" ("item_id" integer not null, "shop_id" integer not null, "quantity" varchar, "price" varchar, "comment" varchar, "deleted_at" datetime, foreign key("item_id") references "items"("id") on delete cascade, foreign key("shop_id") references "shops"("id") on delete cascade, primary key ("item_id", "shop_id"));
CREATE TABLE IF NOT EXISTS "resource_shop" ("resource_id" integer not null, "shop_id" integer not null, "quantity" varchar, "price" varchar, "comment" varchar, "deleted_at" datetime, foreign key("resource_id") references "resources"("id") on delete cascade, foreign key("shop_id") references "shops"("id") on delete cascade, primary key ("resource_id", "shop_id"));
CREATE TABLE IF NOT EXISTS "spells" ("id" integer primary key autoincrement not null, "official_id" varchar, "dofusdb_id" varchar, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar not null, "effect" varchar not null, "effect_array" varchar, "area" integer not null default '0', "level" integer not null default '1', "po" varchar not null default '1', "po_editable" tinyint(1) not null default '1', "pa" varchar not null default '4', "cast_per_turn" varchar not null default '1', "cast_per_target" integer not null default '0', "sight_line" tinyint(1) not null default '1', "number_between_two_cast" varchar not null default '0', "element" integer not null default '0', "category" integer not null default '0', "is_magic" tinyint(1) not null default '1', "powerful" integer not null default '2', "usable" tinyint(1) not null default '0', "is_visible" tinyint(1) not null default '0', "image" varchar, "auto_update" tinyint(1) not null default '1', "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "spells_uniqid_unique" on "spells" ("uniqid");
CREATE TABLE IF NOT EXISTS "spell_invocation" ("spell_id" integer not null, "mob_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("spell_id") references "spells"("id") on delete cascade, foreign key("mob_id") references "mobs"("id") on delete cascade, primary key ("spell_id", "mob_id"));
CREATE TABLE IF NOT EXISTS "spell_type" ("spell_id" integer not null, "spelltype_id" integer not null, "deleted_at" datetime, foreign key("spell_id") references "spells"("id") on delete cascade, foreign key("spelltype_id") references "spelltypes"("id") on delete cascade, primary key ("spell_id", "spelltype_id"));
CREATE TABLE IF NOT EXISTS "specializations" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "description" varchar, "is_visible" tinyint(1) not null default '0', "image" varchar, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "page_id" integer, "created_by" integer, foreign key("page_id") references "pages"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "specializations_uniqid_unique" on "specializations" ("uniqid");
CREATE UNIQUE INDEX "specializations_name_unique" on "specializations" ("name");
CREATE TABLE IF NOT EXISTS "capabilities_specializations" ("capability_id" integer not null, "specialization_id" integer not null, "deleted_at" datetime, foreign key("capability_id") references "capabilities"("id") on delete cascade, foreign key("specialization_id") references "specializations"("id") on delete cascade, primary key ("capability_id", "specialization_id"));
CREATE TABLE IF NOT EXISTS "spelltypes" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "color" varchar not null default 'zinc', "icon" varchar, "is_visible" tinyint(1) not null default '0', "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "attributes" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "description" varchar, "is_visible" tinyint(1) not null default '0', "image" varchar, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "attributes_uniqid_unique" on "attributes" ("uniqid");
CREATE TABLE IF NOT EXISTS "scenarios" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "description" varchar, "slug" varchar not null, "keyword" varchar, "is_public" tinyint(1) not null default '0', "state" integer not null default '0', "is_visible" tinyint(1) not null default '0', "image" varchar, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "scenarios_uniqid_unique" on "scenarios" ("uniqid");
CREATE UNIQUE INDEX "scenarios_slug_unique" on "scenarios" ("slug");
CREATE TABLE IF NOT EXISTS "file_scenario" ("id" integer primary key autoincrement not null, "scenario_id" integer not null, "file" varchar not null, "deleted_at" datetime, foreign key("scenario_id") references "scenarios"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "scenario_page" ("scenario_id" integer not null, "page_id" integer not null, "order_num" integer not null default '0', "visible" tinyint(1) not null default '1', "deleted_at" datetime, foreign key("scenario_id") references "scenarios"("id") on delete cascade, foreign key("page_id") references "pages"("id") on delete cascade, primary key ("scenario_id", "page_id"));
CREATE TABLE IF NOT EXISTS "consumable_scenario" ("consumable_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("consumable_id") references "consumables"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("consumable_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "item_scenario" ("item_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("item_id") references "items"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("item_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "npc_scenario" ("npc_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("npc_id") references "npcs"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("npc_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "scenario_user" ("user_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("user_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "mob_scenario" ("mob_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("mob_id") references "mobs"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("mob_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "scenario_shop" ("shop_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("shop_id") references "shops"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("shop_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "scenario_spell" ("spell_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("spell_id") references "spells"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("spell_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "campaign_scenario" ("campaign_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("campaign_id") references "campaigns"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("campaign_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "resource_scenario" ("resource_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("resource_id") references "resources"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("resource_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "scenario_panoply" ("panoply_id" integer not null, "scenario_id" integer not null, "deleted_at" datetime, foreign key("panoply_id") references "panoplies"("id") on delete cascade, foreign key("scenario_id") references "scenarios"("id") on delete cascade, primary key ("panoply_id", "scenario_id"));
CREATE TABLE IF NOT EXISTS "campaigns" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "description" varchar, "slug" varchar not null, "keyword" varchar, "is_public" tinyint(1) not null default '0', "state" integer not null default '0', "is_visible" tinyint(1) not null default '0', "image" varchar, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "campaigns_uniqid_unique" on "campaigns" ("uniqid");
CREATE UNIQUE INDEX "campaigns_slug_unique" on "campaigns" ("slug");
CREATE TABLE IF NOT EXISTS "file_campaign" ("id" integer primary key autoincrement not null, "campaign_id" integer not null, "file" varchar not null, "deleted_at" datetime, foreign key("campaign_id") references "campaigns"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "campaign_page" ("campaign_id" integer not null, "page_id" integer not null, "order_num" integer not null default '0', "visible" tinyint(1) not null default '1', "deleted_at" datetime, foreign key("campaign_id") references "campaigns"("id") on delete cascade, foreign key("page_id") references "pages"("id") on delete cascade, primary key ("campaign_id", "page_id"));
CREATE TABLE IF NOT EXISTS "consumable_campaign" ("consumable_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("consumable_id") references "consumables"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("consumable_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "item_campaign" ("item_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("item_id") references "items"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("item_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "npc_campaign" ("npc_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("npc_id") references "npcs"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("npc_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "campaign_user" ("user_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("user_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "mob_campaign" ("mob_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("mob_id") references "mobs"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("mob_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "campaign_shop" ("shop_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("shop_id") references "shops"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("shop_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "campaign_spell" ("spell_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("spell_id") references "spells"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("spell_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "resource_campaign" ("resource_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("resource_id") references "resources"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("resource_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "campaign_panoply" ("panoply_id" integer not null, "campaign_id" integer not null, "deleted_at" datetime, foreign key("panoply_id") references "panoplies"("id") on delete cascade, foreign key("campaign_id") references "campaigns"("id") on delete cascade, primary key ("panoply_id", "campaign_id"));
CREATE TABLE IF NOT EXISTS "creatures" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "hostility" integer not null default '2', "location" varchar, "level" integer not null default '1', "other_info" varchar, "life" integer not null default '30', "pa" integer not null default '6', "pm" integer not null default '3', "po" integer not null default '0', "ini" integer not null default '0', "invocation" integer not null default '0', "touch" integer not null default '0', "ca" integer not null default '0', "dodge_pa" integer not null default '0', "dodge_pm" integer not null default '0', "fuite" integer not null default '0', "tacle" integer not null default '0', "vitality" integer not null default '0', "sagesse" integer not null default '0', "strong" integer not null default '0', "intel" integer not null default '0', "agi" integer not null default '0', "chance" integer not null default '0', "do_fixe_neutre" varchar not null default '0', "do_fixe_terre" varchar not null default '0', "do_fixe_feu" varchar not null default '0', "do_fixe_air" varchar not null default '0', "do_fixe_eau" varchar not null default '0', "res_fixe_neutre" text not null default '0', "res_fixe_terre" text not null default '0', "res_fixe_feu" text not null default '0', "res_fixe_air" text not null default '0', "res_fixe_eau" text not null default '0', "res_neutre" integer not null default '0', "res_terre" integer not null default '0', "res_feu" integer not null default '0', "res_air" integer not null default '0', "res_eau" integer not null default '0', "acrobatie_bonus" integer not null default '0', "discretion_bonus" integer not null default '0', "escamotage_bonus" integer not null default '0', "athletisme_bonus" integer not null default '0', "intimidation_bonus" integer not null default '0', "arcane_bonus" integer not null default '0', "histoire_bonus" integer not null default '0', "investigation_bonus" integer not null default '0', "nature_bonus" integer not null default '0', "religion_bonus" integer not null default '0', "dressage_bonus" integer not null default '0', "medecine_bonus" integer not null default '0', "perception_bonus" integer not null default '0', "perspicacite_bonus" integer not null default '0', "survie_bonus" integer not null default '0', "persuasion_bonus" integer not null default '0', "representation_bonus" integer not null default '0', "supercherie_bonus" integer not null default '0', "acrobatie_mastery" integer not null default '0', "discretion_mastery" integer not null default '0', "escamotage_mastery" integer not null default '0', "athletisme_mastery" integer not null default '0', "intimidation_mastery" integer not null default '0', "arcane_mastery" integer not null default '0', "histoire_mastery" integer not null default '0', "investigation_mastery" integer not null default '0', "nature_mastery" integer not null default '0', "religion_mastery" integer not null default '0', "dressage_mastery" integer not null default '0', "medecine_mastery" integer not null default '0', "perception_mastery" integer not null default '0', "perspicacite_mastery" integer not null default '0', "survie_mastery" integer not null default '0', "persuasion_mastery" integer not null default '0', "representation_mastery" integer not null default '0', "supercherie_mastery" integer not null default '0', "kamas" varchar, "drop_" varchar, "other_item" varchar, "other_consumable" varchar, "other_resource" varchar, "other_spell" varchar, "usable" tinyint(1) not null default '1', "is_visible" tinyint(1) not null default '0', "image" varchar, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "creatures_uniqid_unique" on "creatures" ("uniqid");
CREATE TABLE IF NOT EXISTS "capability_creature" ("creature_id" integer not null, "capability_id" integer not null, "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("capability_id") references "capabilities"("id") on delete cascade, primary key ("creature_id", "capability_id"));
CREATE TABLE IF NOT EXISTS "consumable_creature" ("creature_id" integer not null, "consumable_id" integer not null, "quantity" varchar not null default '1', "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("consumable_id") references "consumables"("id") on delete cascade, primary key ("creature_id", "consumable_id"));
CREATE TABLE IF NOT EXISTS "creature_item" ("creature_id" integer not null, "item_id" integer not null, "quantity" varchar not null default '1', "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("item_id") references "items"("id") on delete cascade, primary key ("creature_id", "item_id"));
CREATE TABLE IF NOT EXISTS "creature_spell" ("creature_id" integer not null, "spell_id" integer not null, "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("spell_id") references "spells"("id") on delete cascade, primary key ("creature_id", "spell_id"));
CREATE TABLE IF NOT EXISTS "creature_resource" ("creature_id" integer not null, "resource_id" integer not null, "quantity" varchar not null default '1', "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("resource_id") references "resources"("id") on delete cascade, primary key ("creature_id", "resource_id"));
CREATE TABLE IF NOT EXISTS "attribute_creature" ("creature_id" integer not null, "attribute_id" integer not null, "deleted_at" datetime, foreign key("creature_id") references "creatures"("id") on delete cascade, foreign key("attribute_id") references "attributes"("id") on delete cascade, primary key ("creature_id", "attribute_id"));
CREATE TABLE IF NOT EXISTS "mobraces" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "name" varchar not null, "super_race" varchar, "is_visible" tinyint(1) not null default '0', "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "mobraces_uniqid_unique" on "mobraces" ("uniqid");
CREATE TABLE IF NOT EXISTS "notifications" ("id" varchar not null, "type" varchar not null, "notifiable_type" varchar not null, "notifiable_id" integer not null, "data" text not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, primary key ("id"));
CREATE INDEX "notifications_notifiable_type_notifiable_id_index" on "notifications" ("notifiable_type", "notifiable_id");
CREATE TABLE IF NOT EXISTS "panoplies" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "description" varchar, "bonus" varchar, "usable" tinyint(1) not null default '1', "is_visible" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "created_by" integer, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "panoplies_uniqid_unique" on "panoplies" ("uniqid");
CREATE TABLE IF NOT EXISTS "resourcetypes" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE UNIQUE INDEX "resourcetypes_uniqid_unique" on "resourcetypes" ("uniqid");
CREATE TABLE IF NOT EXISTS "consumabletypes" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE UNIQUE INDEX "consumabletypes_uniqid_unique" on "consumabletypes" ("uniqid");
CREATE TABLE IF NOT EXISTS "itemtypes" ("id" integer primary key autoincrement not null, "uniqid" varchar not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime);
CREATE UNIQUE INDEX "itemtypes_uniqid_unique" on "itemtypes" ("uniqid");
